pattern.code:
match [
# shot 1: some person "a", scale s1
{faces = [{id = a}], scale = s1, experimental = {similarityNext1 = {xclip > 0.2}}},
# shot 2: same person "a", smaller scale
{faces = [{id = a}], scale < s1}
] from shots

match [
{faces = [{id = a}], scale = s1},
{faces = [{id = a}], scale < s1, scale = s2},
{faces = [{id = a}], scale < s2}
] from shots

match [
{faces = [{id = a, gaze = g1}]},
{faces = [{id = b, gaze = g2}]},
{faces = [{id = a, gaze = g3}]}
] from shots where a != b and all(lambda g: abs(g.h) > 2.5, [g1, g2, g3])

match [
{faces = [{id = a}, {region = ra}]},
{faces = [{id = b}, {region = rb}]}
] from shots
where a != b and region_relation(ra, rb) == SAME

match [
{faces = [{id = a}, {region = ra}]},
{faces = [{id = b}, {region = rb}]}
] from shots
where a != b and region_relation(ra, rb) == DIFFERENT

match [
{faces = [{id = a}], scale <= MEDIUM_SHOT, st = st1},
{faces = [{id = a}], scale <= MEDIUM_SHOT}
] from shots join speakerTurns as st
where max(st1.words.propn) > 0
